-- ÉTAPE 3 : REQUÊTONS -------------------------- Niveau facile ------------------------CREATE TABLE users (    id INTEGER PRIMARY KEY AUTOINCREMENT,    firstname TEXT NOT NULL,    name TEXT NOT NULL,    email TEXT NOT NULL UNIQUE);INSERT INTO users (firstname, name, email) VALUES    ('Ada', 'Lovelace', 'ada@codepassport.dev'),    ('Grace', 'Kelly', 'grace@codepassport.dev'),    ('Dorothy', 'Passeport', 'dorothy@codepassport.dev'),    ('Vi', 'Violet', 'vi@codepassport.dev');    ('Marie', 'Curie', 'marie@codepassport.dev'),    ('Sophia', 'Garcia', 'sophia@codepassport.dev'),    ('Emma', 'Watson', 'emma@codepassport.dev'),    ('Luna', 'Chen', 'luna@codepassport.dev'),    ('Olivia', 'Smith', 'olivia@codepassport.dev');CREATE TABLE edusign (    id INTEGER PRIMARY KEY AUTOINCREMENT,    signet_at DATETIME NOT NULL,    users_id INTEGER REFERENCES users(id));---- 1.Sélectionner toutes les lignes de la table users ----SELECT * FROM users;---- 2.Sélectionner uniquement la ligne où le prénom est Ada dans la table users ----SELECT * FROM usersWHERE firstname = 'Ada';---- 3.Sélectionner les lignes dont la première lettre du prénom est un B dans la table users ----INSERT INTO users (firstname, name, email) VALUES    ('Baba', 'Lovelace', 'baba@codepassport.dev')SELECT * FROM usersWHERE firstname LIKE 'B%';---- 4.Compter le nombre de ligne qu’il y a dans la table users ----SELECT COUNT(*) FROM users;---- 5.Compter le nombre de ligne dont la première lettre du prénom est un B dans la table users ----SELECT COUNT(*) FROM usersWHERE firstname LIKE 'B%';---- 6.Afficher uniquement la colonne contenant le prénom de la table users ----SELECT firstname FROM users;----------------------- Niveau moyen --------------------------- Insérer une ligne dans la table edusign qui correspond à la user Ada avec comme date de signature le 2024-05-30 09:30:00 ----INSERT INTO edusign (users_id, signet_at) VALUES(1, '2024-05-30 09:30:00'); --> problème avec cette commande : on la date ne se rajoute pas automatiquement ------ Insérer une ligne dans la table edusign qui correspond à la user Bella avec comme date de signature le 2024-05-30 09:30:00 ----INSERT INTO users (firstname, name, email) VALUES    ('Bella', 'Lovelace', 'bella@codepassport.dev')INSERT INTO edusign (users_id, signet_at) VALUES(11, '2024-05-30 09:30:00'); --> problème avec cette commande : on la date ne se rajoute pas automatiquement ------ Insérer toutes les lignes de la table users dans la table edusign avec pour date de signature le 2024-09-01 09:30:00 ----INSERT INTO edusign (users_id, signet_at) VALUES    (2, '2024-05-30 09:30:00'),    (3, '2024-05-30 09:30:00'),    (4, '2024-05-30 09:30:00'),    (5, '2024-05-30 09:30:00'),    (6, '2024-05-30 09:30:00'),    (7, '2024-05-30 09:30:00'),    (8, '2024-05-30 09:30:00'),    (9, '2024-05-30 09:30:00'),    (10, '2024-05-30 09:30:00');---- 4. Sélectionner toutes les lignes de la table edusign ordonnées par date de signature de la plus récente à la plus---- ancienne et par user_id ascendant ----SELECT * FROM edusign    ORDER BY signet_at DESC, id ASC;---- 5.Sélectionner toutes les lignes de la table edusign dont la date est 2024-05-30 09:30:00 ----SELECT * FROM edusign    WHERE signet_at = '2024-05-30 09:30:00';------------------------------- Niveau moyen + (bonus) ----------------------------------- 1. Dans la table edusign, afficher le nombre d’apprenantes par date. ----SELECT COUNT(*) FROM edusign    WHERE signet_at = '2024-09-01 09:30:00'; --> Dans le cas où il y a une date signature par apprenant (on ne peut    ---- avoir un apprenant qui signe pour le même jour et la même heure) ----SELECT signed_at, COUNT(*) as nombre_users    FROM edusign    GROUP BY signed_at;---- Compter le nombre de ligne au sein de la table edusign dont le prénom est Bella, le nom de la colonne finale doit---- être volume -------- Pur que ça plus intéressant pour moi, je suis partie sur name = 'Lovelace' ----SELECT    e.users_id,    u.name,    COUNT(*) AS volumeFROM edusign e, users uWHERE e.users_id = u.id AND u.name = 'Lovelace';